import React from "react";

import "./Input.css";

const Input = () => (
  <form className="form" action="">
    <input
      className="input"
      type="text"
      placeholder="Type a message..."
      value={message}
      onChange={(e) => setMessage(e.target.value)}
      onKeyPress={(e) => (e.key === "Enter" ? sendMessage(e) : null)}
    />
    <button></button>
  </form>
);

export default Input;
